    }


    // console.log(result())

    const getValue = (e) => {
        e.preventDefault();
        console.log(result());
    }

    const calcVal = (v, r) => {
        console.log(+v * r);
    }

    return (
        <>
            <form action="" onSubmit={getValue}>
                <input placeholder="Введите"
                    value={value}
                    onChange={(e) => setValue(e.target.value)}
                />
                <button>Конвертировать</button>
            </form>

            <span>{getValue}</span>
        </>
    )
}
