import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import './App.css';

import Input from "./components/Input";
import Header from "./components/Header";

// import Select from './components/Select';




function App() {
  const [value, setValue] = useState("");
  // const [curr, setCurr] = useState(0);
  // const [select, setSelect] = useState("EUR")
  const [data, setData] = useState({
    input: 0,
    arrData: [],
    activeObj: {},
    currency: null,
    // rate: 33,
  });

  async function getArray() {
    axios.get("https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json")
      .then(response => response.data)
      .then(arr => setData({ ...data, arrData: arr }))
    // .then(data => data.find(item => item.cc === select.currency))
    // .then(object => object.rate)
    // .then(rate => setCurr(rate));
  }

  useEffect(() => {
    getArray();
    // getRate(data.arrData);
  }, [data.currency]);


  // function getRate(arr) {
  //   const rate = arr.find(item => item.cc === data.currency);
  //   return setData({ ...data, rate: rate });
  // };

  // function getRate(e) {
  //   const val = console.log(e.target.value);
  //   console.log(data.arrData[val])

  // console.log(data.arrData.find(item => item.cc === val))
  // const rate = arr.find(item => item.cc === data.currency);
  // return setData({ ...data, rate: rate });
  // };

  // getActiveObj(o){
  //   const pp = o.map(item => item.cc)
  // }

  console.log(data);
  // const result = +value.replace(/[^\d]/g) ? (+value * select.input).toFixed(1) : "";
  return (
    <Fragment>
      <div className="wrapper">
        <Header />
        <div className="inner">
          <div className="block">
            <Input value={+value.replace(/[^\d]/g) ? +value : ""} setValue={setValue} />
            <span className="uah">UAH</span>
          </div>
          <div className="block">
            <Input value={value * data} />
            {/* <Select select={select.currency} setSelect={setSelect} /> */}
            {/* <Select selectArray={select.arrData} selectValue={select.currency} setSelect={setSelect} /> */}
            <select
              value={data.currency} onChange={e => console.log(e)}
              // {e => setData({ ...data, currency: e.target.value, rate: e.target.rate })}
              className="select">
              {/* {data.arrData.map(item => {
                return <option key={item.cc} data-rate={item.rate}>{item.cc}</option>
              })} */}
            </select>
            {/* <div>1 гривня = {select.rate}</div> */}
          </div>
        </div>
      </div>
    </Fragment >
  )
}

export default App;
