import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import './App.css';
import Input from "./components/Input";
import Header from "./components/Header";
import Select from './components/Select';



function App() {
  const [inputValue, setInputValue] = useState({
    valueOfItem: "",
    name: ""
  });

  const [select, setSelect] = useState({
    arrayOfItems: [],
    currency: "EUR",
    activeObj: {
      rate: 0,
      txt: ""
    },
  })

  const [themeLight, setTheme] = useState(true);
  const p = themeLight === !true ? document.body.classList.add("dark") : document.body.classList.remove("dark");


  async function getRate() {
    axios.get("https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json")
      .then(response => response.data)
      .then(data => setSelect({ ...select, arrayOfItems: data, activeObj: data.find(item => item.cc === select.currency) }))
      .catch(e => console.log(e))
  }

  useEffect(() => {
    getRate();
  }, [select.currency])


  const changeRate = (e) => {
    setSelect({ ...select, currency: e.target.value });
  }

  const ua = inputValue.name === "ua" ? (inputValue.valueOfItem / select.activeObj.rate).toFixed(2) : inputValue.valueOfItem;
  const etc = inputValue.name === "etc" ? (inputValue.valueOfItem * select.activeObj.rate).toFixed(2) : inputValue.valueOfItem;



  console.log(themeLight)
  return (
    <Fragment>
      <input type="checkbox" />
      <button onClick={() => setTheme(!themeLight)}> Изменить тему</button>
      <svg version="1.0" xmlns="http://www.w3.org/2000/svg"
        width="1049.000000pt" height="1280.000000pt" viewBox="0 0 1049.000000 1280.000000"
        preserveAspectRatio="xMidYMid meet">
        <g transform="translate(0.000000,1280.000000) scale(0.100000,-0.100000)"
          fill="#000000" stroke="none">
          <path d="M6209 12796 c-2 -2 -83 -9 -179 -16 -1070 -70 -2026 -365 -2925 -903
-638 -382 -1225 -898 -1699 -1492 -295 -369 -521 -723 -730 -1142 -339 -683
-551 -1410 -630 -2163 -56 -527 -54 -871 5 -1405 132 -1186 603 -2320 1355
-3261 1107 -1385 2753 -2258 4494 -2384 85 -6 199 -15 253 -21 126 -12 351
-12 491 1 61 5 179 14 261 20 83 6 220 20 305 31 85 11 189 24 230 29 82 8
438 88 650 145 584 157 1221 441 1750 779 241 153 633 447 647 483 4 10 -3 24
-19 38 -24 20 -39 22 -229 28 -2140 69 -4094 1227 -5199 3082 -779 1308 -1052
2893 -759 4405 269 1386 998 2635 2087 3574 83 72 102 93 102 115 0 51 -24 61
-147 61 -60 0 -112 -2 -114 -4z"/>
        </g>
      </svg>
      <div className="wrapper">
        <Header />
        <div className="inner">
          <div className="block">
            <Input name="etc" rate={ua} onChange={e => setInputValue({
              name: "etc", valueOfItem: +e.target.value.replace(/[^\d]/g) ? +e.target.value.replace(/[^\d]/g) : ""
            })} />
            <Select currency={select.currency} changeRate={changeRate} arrayOfItems={select.arrayOfItems} />
          </div>
          <div className="block">
            <Input name="ua" rate={etc} onChange={e => setInputValue({
              name: "ua", valueOfItem: +e.target.value.replace(/[^\d]/g) ? +e.target.value.replace(/[^\d]/g) : ""
            })} />
            <span className="uah">UAH</span>
            <div>{`1 ${select.activeObj.txt.toLowerCase()} - ${(select.activeObj.rate).toFixed(2)}  гривень`}</div>

          </div>
          <button
            className="clear"
            onClick={() => setInputValue({ valueOfItem: "", name: "" })}
          >Очистити </button>
        </div>
      </div>
    </Fragment >
  )
}

export default App;
