  //   setCurr(value);
  //   console.log(curr);
  //   console.log(value);
  // }

  const result = (curr * +value).toFixed(1);
  return (
    <Fragment>
      <div className="wrapper">
        <Header />
        <div>
          <Input value={value} setValue={setValue} curr={curr} />
          <Select select={select} setSel={setSel} />
        </div>
        <div>
          <Input value={result} />
          {/* <Select select={select} setSel={setSel} /> */}
          {/* <input value={result} /> */}
          <Input>{(curr * +value).toFixed(1)}</Input>
        </div>

      </div>
    </Fragment >
  )
}

export default App;
