    // }


    return (
        <>       <form action="" >
            <input placeholder="Введите"
                value={value}
                onChange={(e) => setValue(e.target.value)}
            />
            <button>Конвертировать</button>
        </form>
            <span onClick={() => r(value)}>Результат: {r(value)}</span></>
    )
}
