import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import './App.css';
import Input from "./components/Input";
import Header from "./components/Header";

import Select from './components/Select';




function App() {
  const [value, setValue] = useState("");
  const [curr, setCurr] = useState(0);
  const [select, setSel] = useState("EUR")

  async function getRate(v2) {
    axios.get("https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json")
      .then(response => response.data)
      .then(data => data.find(item => item.cc === select))
      .then(object => object.rate)
      .then(rate => setCurr(rate));
  }


  useEffect(() => {
    getRate(value);
  }, [select])

  // const reverse = () => {
  //   setValue(result);
  //   setCurr(value);
  //   console.log(curr);
  //   console.log(value);
  // }

  const result = (curr * +value).toFixed(1);
  return (
    <Fragment>
      <div className="wrapper">
        <Header />
        <div className="inner">
          <div className="block">
            <Input value={value} setValue={setValue} curr={curr} />
          </div>
          <div className="block">
            <Input value={result} />
            <Select select={select} setSel={setSel} />
          </div>
        </div>
      </div>
    </Fragment >
  )
}

export default App;
