    }, [value])

    console.log(rate())

    const getValue = (e) => {
        e.preventDefault();
    }

    const calcVal = (v, r) => {
        console.log(+v * r);
        console.log(typeof v);
        console.log(typeof +r);
    }

    return (
        <>
            <form action="" onSubmit={getValue}>
                <input placeholder="Введите"
                    value={value}
                    onChange={(e) => setValue(e.target.value)}
                />
                <button>Конвертировать</button>
            </form>

            <span></span>
        </>
    )
}
