import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import './App.css';
import Input from "./components/Input";
import Header from "./components/Header";

import Select from './components/Select';




function App() {
  const [value, setValue] = useState({
    value1: "",
    value2: ""
  });
  const [curr, setCurr] = useState(0);
  const [select, setSelect] = useState({
    array: [],
    currency: "EUR",
    activeObj: {
      rate: 0
    },
  })

  async function getRate() {
    axios.get("https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json")
      .then(response => response.data)
      // .then(data => data.find(item => item.cc === select.currency))
      // .then(obj => setSelect({ ...select, array: obj, activeObj: obj, rate: obj.rate }))
      .then(data => setSelect({ ...select, array: data, activeObj: data.find(item => item.cc === select.currency) }))



    // .then(object => object.rate)
    // .then(rate => setCurr(rate));
  }

  useEffect(() => {
    getRate();
  }, [select.currency])


  let changeNum = () => {
    setValue({ ...value, value2: value.value1 + 2 })
    // console.log(5)
  }

  console.log(value.value2)
  // const result = +value.replace(/[^\d]/g) ? (+value * curr).toFixed(1) : "";


  // let changeRes = (e) => {
  //   setSelect({ ...select, currency: e.target.value });
  //   changeNum();
  // }

  console.log(value.value1, value.value2)
  return (
    <Fragment>
      <div className="wrapper">
        <Header />
        <div className="inner">
          <div className="block">
            {/* <Input value={value.value1} setValue={setFirstValue} /> */}
            <input
              className="input"
              onChange={e => setValue({ ...value, value1: e.target.value, value2: +e.target.value * select.activeObj.rate })}
              value={value.value1} />
            <span className="uah">UAH</span>
          </div>
          <div className="block">
            <input
              className="input"
              // onChange={changeNum}
              value={value.value2} />
            {/* <Input value={value.value2} setValue={setValue} /> */}
            {/* <Select select={select.currency} setSelect={setSelect} /> */}
            <select value={select.currency}
              className="select"
              onChange={e => setSelect({ ...select, currency: e.target.value })}>
              {select.array.map(item => {
                return <option key={item.cc}>{item.cc}</option>
              })
              }
            </select>

            <div>1 гривня = {(select.activeObj.rate).toFixed(2)}</div>
          </div>
        </div>
      </div>
    </Fragment >
  )
}

export default App;
