import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import './App.css';
import Input from "./components/Input";
import Header from "./components/Header";

import Select from './components/Select';




function App() {
  const [inputValue, setInputValue] = useState({
    // value1: "",
    // value2: ""
    value1: "",
    name: "ua"

  });

  const [select, setSelect] = useState({
    array: [],
    currency: "EUR",
    activeObj: {
      rate: 0,
      txt: ""
    },
  })

  async function getRate() {
    axios.get("https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json")
      .then(response => response.data)
      // .then(data => data.find(item => item.cc === select.currency))
      // .then(obj => setSelect({ ...select, array: obj, activeObj: obj, rate: obj.rate }))
      .then(data => setSelect({ ...select, array: data, activeObj: data.find(item => item.cc === select.currency) }))



    // .then(object => object.rate)
    // .then(rate => setCurr(rate));
  }

  useEffect(() => {
    getRate();
  }, [select.currency])



  function toUa() {
    return inputValue.value1 / select.activeObj.rate;
  }
  function toEtc() {
    return inputValue.value1 * select.activeObj.rate;
  }


  let changeRes = (e) => {
    setSelect({ ...select, currency: e.target.value });
    // setInputValue({
    //   ...inputValue,
    //   // value2: value.value1 * select.activeObj.rate
    // })
  }



  return (
    <Fragment>
      <div className="wrapper">
        <Header />
        <div className="inner">
          <div className="block">
            <input
              value1="ua"
              className="input"
              value={inputValue.name === "etc" ? ((inputValue.value1 * select.activeObj.rate).toFixed(2)) : inputValue.value1}
              onChange={e => setInputValue({ name: "ua", value1: e.target.value })}
            />
            <span className="uah">UAH</span>
          </div>
          <div className="block">
            <input
              value1="etc"
              className="input"
              value={inputValue.name === "ua" ? (inputValue.value1 * select.activeObj.rate).toFixed(2) : inputValue.value1}
              onChange={e => setInputValue({ name: "etc", value1: e.target.value })}
            />
            <select value={select.currency}
              className="select"
              onChange={changeRes}>
              {select.array.map(item => {
                return <option key={item.cc}>{item.cc}</option>
              })
              }
            </select>

            <div>1 гривня - {(select.activeObj.rate).toFixed(2) + " " + select.activeObj.txt.toLowerCase()}</div>
          </div>
          <button
            className="input"
            onClick={() => inputValue.value1 = ""}
          >Очистити </button>
        </div>
      </div>
    </Fragment >
  )
}

export default App;
