import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import './App.css';
import Input from "./components/Input";
import Header from "./components/Header";

import Select from './components/Select';




function App() {
  const [value, setValue] = useState("");
  const [curr, setCurr] = useState(0);
  const [select, setSel] = useState("EUR")

  async function getRate(num) {
    axios.get("https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json")
      .then(response => response.data)
      .then(data => data.find(item => item.cc === select))
      .then(object => object.rate)
      .then(rate => setCurr(rate));
  }


  useEffect(() => {
    getRate(value);
  }, [select])

  // const reverse = () => {
  //   setValue(result);
  //   setCurr(value);
  //   console.log(curr);
  //   console.log(value);
  // }

  const result = (curr * +value).toFixed(1);
  return (
    <Fragment>
      <div className="wrapper">
        <Header />
        <div>
          <Input value={value} setValue={setValue} curr={curr} />
          <select value={select} onChange={(e) => setSel(e.target.value)}>
            <option>USD - долар США</option>
            <option>EUR - євро</option>
            <option>RUB - російський рубль</option>
            <option>PLN - злотий</option>
            <option>AUD - австралійський долар</option>
            <option>CAD</option>
            <option>CNY</option>
            <option>HRK</option>
            <option>CZK</option>
            <option>DKK</option>
            <option>HKD</option>


            <option>DKK</option>
            <option>DKK</option>
            <option>DKK</option>

          </select>
          {/* <Select select={select} setSel={setSel} /> */}
        </div>
        <div>
          {/* <Input value={result} /> */}
          {/* <Select select={select} setSel={setSel} /> */}
          {/* <input value={result} /> */}
          <input value={result} />
        </div>

      </div>
    </Fragment >
  )
}

export default App;
