import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import './App.css';
import Input from "./components/Input";
import Header from "./components/Header";

import Select from './components/Select';




function App() {
  const [value, setValue] = useState({
    value1: "",
    value2: 0
  });
  const [curr, setCurr] = useState(0);
  // const [select, setSelect] = useState("EUR")
  const [select, setSelect] = useState({
    currency: "EUR",
    activeObj: {},
    rate: 33.525
  })

  async function getRate(v2) {
    axios.get("https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json")
      .then(response => response.data)
      .then(data => data.find(item => item.cc === select.currency))
      .then(obj => setSelect({ ...select, activeObj: obj, rate: obj.rate }))
    // .then(object => object.rate)
    // .then(rate => setCurr(rate));
  }

  useEffect(() => {
    getRate(value);
  }, [select.currency])

  console.log(value.value1 + " value")

  // const result = +value.replace(/[^\d]/g) ? (+value * curr).toFixed(1) : "";
  return (
    <Fragment>
      <div className="wrapper">
        <Header />
        <div className="inner">
          <div className="block">
            <Input value={value.value1} setValue={setValue} />
            <span className="uah">UAH</span>
          </div>
          <div className="block">
            <Input value={value.value2} setValue={setValue} />
            <Select select={select.currency} setSelect={setSelect} />
            <div>1 гривня = {(select.rate).toFixed(2)}</div>
          </div>
        </div>
      </div>
    </Fragment >
  )
}

export default App;
