import React, { Fragment, useState, useEffect } from "react";



import './App.css';
import Input from "./components/Input";
import Header from "./components/Header";

function App() {
  const [value, setValue] = useState("");
  const [curr, setCurr] = useState(0);

  async function getRate(v2) {
    let response = await fetch("https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json");
    let jsonResponse = await response.json();
    let search = await jsonResponse.find(item => item.cc === "USD");
    let val = await search.rate;
    return setCurr(val);
  }

  useEffect(() => {
    getRate(value);
  }, [])

  return (
    <Fragment>
      <div className="wrapper">
        <Header />
        <Input value={value} setValue={setValue} curr={curr} />
        <select></select>
        <span >Результат:{(curr * +value).toFixed(2)}</span>
      </div>
    </Fragment>
  )
}

export default App;
