import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import './App.css';
import Input from "./components/Input";
import Header from "./components/Header";
import Select from './components/Select';



function App() {
  const [inputValue, setInputValue] = useState({
    valueOfItem: "",
    name: ""
  });

  const [select, setSelect] = useState({
    arrayOfItems: [],
    currency: "EUR",
    activeObj: {
      rate: 0,
      txt: ""
    },
  })

  const [themeLight, setTheme] = useState(true);



  let p = themeLight === false ? document.body.classList.add("dark") : document.body.classList.remove("dark");


  async function getRate() {
    axios.get("https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json")
      .then(response => response.data)
      .then(data => setSelect({ ...select, arrayOfItems: data, activeObj: data.find(item => item.cc === select.currency) }))
      .catch(e => console.log(e))
  }

  useEffect(() => {
    getRate();
  }, [select.currency])


  const changeRate = (e) => {
    setSelect({ ...select, currency: e.target.value });
  }

  const ua = inputValue.name === "ua" ? (inputValue.valueOfItem / select.activeObj.rate).toFixed(2) : inputValue.valueOfItem;
  const etc = inputValue.name === "etc" ? (inputValue.valueOfItem * select.activeObj.rate).toFixed(2) : inputValue.valueOfItem;




  return (
    <Fragment>
      <div className="container">

        {/* <div className="checkbox">
          <input className="checkbox__input" name="p" type="radio" id="checkbox_1" onChange={() => setTheme(!themeLight)} />
          <input className="checkbox__input" name="p" type="radio" id="checkbox_1" onChange={() => setTheme(!themeLight)} /> */}
        {/* <label for="checkbox_1" className="checkbox__label"></label> */}
        {/* </div> */}
        {/* <div className="radio-inner">
          <input type="radio" name="a" className="radio1" />
          <label className="label">1</label>
          <input type="radio" name="a" className="radio2" />

        </div> */}

        <div className="theme">

          <input className="theme__input" name="p" type="checkbox" id="checkbox" onChange={() => setTheme(!themeLight)} />
          <label for="checkbox" className="theme__label">
            <symbol id="icon-moon-o" viewBox="0 0 23 28">
              <path d="M19.719 20.359c-0.562 0.094-1.141 0.141-1.719 0.141-5.797 0-10.5-4.703-10.5-10.5 0-1.984 0.578-3.922 1.625-5.578-4.156 1.234-7.125 5.047-7.125 9.578 0 5.516 4.484 10 10 10 3.016 0 5.844-1.375 7.719-3.641zM22.891 19.031c-1.953 4.234-6.234 6.969-10.891 6.969-6.609 0-12-5.391-12-12 0-6.484 5.078-11.75 11.547-11.984 0.438-0.016 0.797 0.234 0.953 0.609 0.172 0.391 0.063 0.844-0.234 1.125-1.781 1.625-2.766 3.844-2.766 6.25 0 4.688 3.813 8.5 8.5 8.5 1.234 0 2.422-0.266 3.563-0.797 0.391-0.172 0.828-0.094 1.125 0.203s0.375 0.75 0.203 1.125z"></path>
            </symbol>

          </label>
        </div>



        {/* <input className={!themeLight ? "b" : "a"} type="checkbox" onClick={() => setTheme(!themeLight)} /> */}


        <div className="wrapper">
          {/* <div>{ }</div> */}
          <Header />
          <div className="inner">
            <div className="block">
              <Input name="etc" rate={ua} onChange={e => setInputValue({
                name: "etc", valueOfItem: +e.target.value.replace(/[^\d]/g) ? +e.target.value.replace(/[^\d]/g) : ""
              })} />
              <Select currency={select.currency} changeRate={changeRate} arrayOfItems={select.arrayOfItems} />
            </div>
            <div className="block">
              <Input name="ua" rate={etc} onChange={e => setInputValue({
                name: "ua", valueOfItem: +e.target.value.replace(/[^\d]/g) ? +e.target.value.replace(/[^\d]/g) : ""
              })} />
              <span className="uah">UAH</span>
              <div className="text">{`1 ${select.activeObj.txt.toLowerCase()} - ${(select.activeObj.rate).toFixed(2)}  гривень`}</div>

            </div>
            <button
              className="clear"
              onClick={() => setInputValue({ valueOfItem: "", name: "" })}
            >Очистити </button>
          </div>
        </div>
      </div>
    </Fragment >
  )
}

export default App;
