import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import './App.css';

import Input from "./components/Input";
import Header from "./components/Header";

// import Select from './components/Select';




function App() {
  const [value, setValue] = useState("");
  // const [curr, setCurr] = useState(0);
  // const [select, setSelect] = useState("EUR")
  const [data, setData] = useState({
    input: 0,
    arrData: [],
    currency: "EUR"
    // // rate: 33.525
  });

  async function getRate(obj) {
    axios.get("https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json")
      .then(response => response.data)
      .then(arr => setData({ ...obj, arrData: arr }))
    // .then(data => data.find(item => item.cc === select.currency))
    // .then(object => object.rate)
    // .then(rate => setCurr(rate));
  }

  useEffect(() => {
    getRate(data);
    rate(data.arrData)
  }, []);


  function rate(arr) { arr.find(item => item.includes(data.currency)) };
  console.log(rate)

  console.log(data);
  // const result = +value.replace(/[^\d]/g) ? (+value * select.input).toFixed(1) : "";
  return (
    <Fragment>
      <div className="wrapper">
        <Header />
        <div className="inner">
          <div className="block">
            <Input value={+value.replace(/[^\d]/g) ? +value : ""} setValue={setValue} />
            <span className="uah">UAH</span>
          </div>
          <div className="block">
            <Input value={data.input} />
            {/* <Select select={select.currency} setSelect={setSelect} /> */}
            {/* <Select selectArray={select.arrData} selectValue={select.currency} setSelect={setSelect} /> */}
            <select
              value={data.currency}
              className="select">
              {data.arrData.map(item => {
                return <option key={item.cc}>{item.cc}</option>
              })}
            </select>
            {/* <div>1 гривня = {select.rate}</div> */}
          </div>
        </div>
      </div>
    </Fragment >
  )
}

export default App;
