import React, { Fragment, useState, useEffect } from "react";



import './App.css';
import Input from "./components/Input";
import Header from "./components/Header";

function App() {
  const [value, setValue] = useState("");
  const [curr, setCurr] = useState(0);
  const [select, setSel] = useState("EUR")

  async function getRate(v2) {
    let response = await fetch("https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json");
    let jsonResponse = await response.json();
    let search = await jsonResponse.find(item => item.cc === select);
    let val = await search.rate;
    return setCurr(val);
  }

  useEffect(() => {
    getRate(value);
  }, [select])

  return (
    <Fragment>
      <div className="wrapper">
        <Header />
        <Input value={value} setValue={setValue} curr={curr} />
        <select value={select} onChange={(e) => setSel(e.target.value)}>
          <option>USD</option>
          <option>EUR</option>
          <option>AUD</option>
          <option>CAD</option>
          <option>CNY</option>




        </select>
        <span >Результат:{(curr * +value).toFixed(2)}</span>
      </div>
    </Fragment>
  )
}

export default App;
