import React, { useState, useEffect } from "react";

export default function Input() {
    const [value, setValue] = useState("");

    useEffect(() => {
        let rate = fetch("https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json")
            .then(response => response.json())
            .then(data => {
                return data.find(item => item.cc === "USD");
            })
            .then(value => value.rate);
        let t = rate.then(num => calcVal(num, value))
        // .then(num => calcVal(num, value));
    }, [value])

    console.log(t + " t")

    const getValue = (e) => {
        e.preventDefault();
    }

    const calcVal = (v, r) => {
        console.log(+v * r);
        console.log(typeof v);
        console.log(typeof +r);
    }

    return (
        <>
            <form action="" onSubmit={getValue}>
                <input placeholder="Введите"
                    value={value}
                    onChange={(e) => setValue(e.target.value)}
                />
                <button>Конвертировать</button>
            </form>

            <span></span>
        </>
    )
}
