import React, { Fragment, useState, useEffect } from "react";
import axios from "axios";
import './App.css';
import Input from "./components/Input";
import Header from "./components/Header";

import Select from './components/Select';




function App() {
  const [inputValue, setInputValue] = useState({
    valueOfItem: "",
    name: "ua"

  });

  const [select, setSelect] = useState({
    arrayOfItems: [],
    currency: "EUR",
    activeObj: {
      rate: 0,
      txt: ""
    },
  })

  async function getRate() {
    axios.get("https://bank.gov.ua/NBUStatService/v1/statdirectory/exchange?json")
      .then(response => response.data)
      .then(data => setSelect({ ...select, arrayOfItems: data, activeObj: data.find(item => item.cc === select.currency) }))



    // .then(object => object.rate)
    // .then(rate => setCurr(rate));
  }

  useEffect(() => {
    getRate();
  }, [select.currency])



  function toUa() {
    return inputValue.valueOfItem / select.activeObj.rate;
  }
  function toEtc() {
    return inputValue.valueOfItem * select.activeObj.rate;
  }


  let changeRes = (e) => {
    setSelect({ ...select, currency: e.target.value });
  }



  const ua = inputValue.name === "etc" ? (inputValue.valueOfItem * select.activeObj.rate).toFixed(2) : inputValue.valueOfItem;


  return (
    <Fragment>
      <div className="wrapper">
        <Header />
        <div className="inner">
          <div className="block">
            <input
              name="ua"
              className="input"
              value={null}
              onChange={e => setInputValue({ name: "ua", valueOfItem: e.target.value })}
            />
            <span className="uah">UAH</span>
          </div>
          <div className="block">
            <input
              name="etc"
              className="input"
              value={inputValue.name === "ua" ? (inputValue.valueOfItem * select.activeObj.rate).toFixed(2) : inputValue.valueOfItem}
              onChange={e => setInputValue({ name: "etc", valueOfItem: e.target.value })}
            />
            <select value={select.currency}
              className="select"
              onChange={changeRes}>
              {select.arrayOfItems.map(item => {
                return <option key={item.cc}>{item.cc}</option>
              })
              }
            </select>

            <div>1 гривня - {(select.activeObj.rate).toFixed(2) + " " + select.activeObj.txt.toLowerCase()}</div>
          </div>
          <button
            className="input"
            onClick={() => inputValue.valueOfItem = ""}
          >Очистити </button>
        </div>
      </div>
    </Fragment >
  )
}

export default App;
